import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import verifierAbi from "./abi/DocumentVerifier.json";
import registryAbi from "./abi/IssuerRegistry.json";
import "./App.css";

const VERIFIER_ADDRESS = "0xe7f1725E7734CE288F8367e1Bb143E90bb3F0512";
const REGISTRY_ADDRESS = "0x5FbDB2315678afecb367f032d93F642f64180aa3";

export default function App() {
  const [account, setAccount] = useState("");
  const [issuer, setIssuer] = useState({ name: "", organization: "", email: "" });
  const [issuersList, setIssuersList] = useState([]);
  const [selectedIssuer, setSelectedIssuer] = useState("");
  const [search, setSearch] = useState("");
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");
  const [loading, setLoading] = useState(false);

  const getProvider = async () => {
    const provider = new ethers.BrowserProvider(window.ethereum);
    provider.getNetwork = async () => ({ name: "hardhat", chainId: 31337, ensAddress: null });
    return provider;
  };

  async function connectWallet() {
    if (!window.ethereum) return alert("⚠️ Cài MetaMask trước!");
    const provider = await getProvider();
    const [addr] = await provider.send("eth_requestAccounts", []);
    setAccount(addr);
  }

  async function registerIssuer() {
    try {
      setLoading(true);
      const provider = await getProvider();
      const signer = await provider.getSigner();
      const registry = new ethers.Contract(REGISTRY_ADDRESS, registryAbi.abi, signer);
      const tx = await registry.registerIssuer(issuer.name, issuer.organization, issuer.email);
      setStatus("⏳ Đang gửi giao dịch đăng ký...");
      await tx.wait();
      setStatus("✅ Đăng ký Issuer thành công!");
      await loadIssuers();
    } catch (e) {
      console.error(e);
      setStatus("❌ Lỗi khi đăng ký Issuer.");
    } finally {
      setLoading(false);
    }
  }

  // ✅ Load danh sách Issuer thật
  async function loadIssuers() {
    try {
      const provider = await getProvider();
      const registry = new ethers.Contract(REGISTRY_ADDRESS, registryAbi.abi, provider);
      const addresses = await registry.getAllIssuers();

      const list = [];
      for (let addr of addresses) {
        const [name, org, email] = await registry.getIssuer(addr);
        if (name) list.push({ address: addr, name, org, email });
      }
      setIssuersList(list);
    } catch (e) {
      console.error("Load issuers error:", e);
    }
  }

  useEffect(() => {
    loadIssuers();
  }, []);

  async function storeHash() {
    if (!file) return alert("Chưa chọn file!");
    if (!selectedIssuer) return alert("Chưa chọn Issuer!");
    try {
      setLoading(true);
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      const provider = await getProvider();
      const signer = await provider.getSigner();
      const verifier = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi.abi, signer);

      const tx = await verifier.storeDocumentWithIssuer(hash, selectedIssuer);
      setStatus("⏳ Đang lưu hash...");
      await tx.wait();
      setStatus("✅ Lưu hash thành công!");
    } catch (e) {
      console.error(e);
      if (e.message.includes("Already stored"))
        setStatus("⚠️ Hash đã tồn tại trên blockchain!");
      else setStatus("❌ Lỗi khi lưu hash.");
    } finally {
      setLoading(false);
    }
  }

  async function verifyFile() {
    if (!file) return alert("Chưa chọn file!");
    try {
      setLoading(true);
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));
      const provider = await getProvider();
      const verifier = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi.abi, provider);

      const [exists, issuerAddr, timestamp, name, org] = await verifier.verifyDocument(hash);
      if (!exists) return setStatus("❌ File chưa được lưu trên blockchain.");

      setStatus(
        `✅ File hợp lệ!\nIssuer: ${name} (${org})\nAddress: ${issuerAddr}\nTime: ${new Date(
          Number(timestamp) * 1000
        ).toLocaleString()}`
      );
    } catch (e) {
      console.error(e);
      setStatus("❌ Lỗi khi xác minh file.");
    } finally {
      setLoading(false);
    }
  }

  const filteredIssuers = issuersList.filter(
    (i) =>
      i.name.toLowerCase().includes(search.toLowerCase()) ||
      i.org.toLowerCase().includes(search.toLowerCase()) ||
      i.email.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="container">
      <h1>🧾 Blockchain Document Verifier</h1>
      <button onClick={connectWallet} className="connect-btn">
        {account ? `✅ ${account.slice(0, 6)}...${account.slice(-4)}` : "🔗 Kết nối MetaMask"}
      </button>

      <section>
        <h2>🏢 Đăng ký Issuer</h2>
        <input placeholder="Tên" onChange={(e) => setIssuer({ ...issuer, name: e.target.value })} />
        <input
          placeholder="Tổ chức"
          onChange={(e) => setIssuer({ ...issuer, organization: e.target.value })}
        />
        <input placeholder="Email" onChange={(e) => setIssuer({ ...issuer, email: e.target.value })} />
        <button onClick={registerIssuer} disabled={loading}>
          📝 Đăng ký
        </button>
      </section>

      <section>
        <h2>📁 Xác minh tài liệu</h2>
        <input type="file" onChange={(e) => setFile(e.target.files[0])} />
        <div className="issuer-select">
          <input
            type="text"
            placeholder="🔍 Tìm Issuer..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <select onChange={(e) => setSelectedIssuer(e.target.value)} value={selectedIssuer}>
            <option value="">-- Chọn Issuer --</option>
            {filteredIssuers.map((i, idx) => (
              <option key={idx} value={i.address}>
                {i.name} ({i.org})
              </option>
            ))}
          </select>
        </div>
        <button onClick={storeHash} disabled={loading}>
          📤 Lưu Hash
        </button>
        <button onClick={verifyFile} disabled={loading}>
          🔍 Xác minh
        </button>
      </section>

      <pre className="status-box">{status}</pre>
    </div>
  );
}
