import React, { useState } from "react";
import { ethers } from "ethers";
import verifierAbi from "./abi/DocumentVerifier.json";
import registryAbi from "./abi/IssuerRegistry.json";

// ⚠️ Dán địa chỉ mới sau khi deploy
const VERIFIER_ADDRESS = "0xe7f1725E7734CE288F8367e1Bb143E90bb3F0512";
const REGISTRY_ADDRESS = "0x5FbDB2315678afecb367f032d93F642f64180aa3";

export default function App() {
  const [account, setAccount] = useState("");
  const [issuer, setIssuer] = useState({ name: "", organization: "", email: "" });
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");

  async function connectWallet() {
    if (!window.ethereum) return alert("Cài MetaMask trước!");
    const provider = new ethers.BrowserProvider(window.ethereum);
    const [addr] = await provider.send("eth_requestAccounts", []);
    setAccount(addr);
  }

  async function registerIssuer() {
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({ name: "hardhat", chainId: 31337, ensAddress: null });
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(REGISTRY_ADDRESS, registryAbi, signer);
      const tx = await contract.registerIssuer(issuer.name, issuer.organization, issuer.email);
      setStatus("⏳ Đang gửi giao dịch...");
      await tx.wait();
      setStatus("✅ Đăng ký thành công!");
    } catch (e) {
      console.error(e);
      setStatus("❌ Lỗi khi đăng ký Issuer. Kiểm tra mạng hoặc địa chỉ contract.");
    }
  }

  async function storeHash() {
    if (!file) return alert("Chưa chọn file!");
    try {
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));
      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({ name: "hardhat", chainId: 31337, ensAddress: null });
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi, signer);
      const tx = await contract.storeDocument(hash);
      setStatus("⏳ Đang lưu hash...");
      await tx.wait();
      setStatus("✅ Lưu hash thành công!");
    } catch (e) {
      console.error(e);
      setStatus("❌ Lỗi khi lưu hash (có thể chưa đăng ký Issuer).");
    }
  }

  async function verifyFile() {
    if (!file) return alert("Chưa chọn file!");
    try {
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));
      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({ name: "hardhat", chainId: 31337, ensAddress: null });
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi, signer);

      const [exists, issuerAddr, timestamp, name, org] = await contract.verifyDocument(hash);
      if (!exists) return setStatus("❌ File chưa được lưu trên blockchain.");
      setStatus(
        `✅ File hợp lệ!\nIssuer: ${name} (${org})\nAddress: ${issuerAddr}\nTime: ${new Date(
          Number(timestamp) * 1000
        ).toLocaleString()}`
      );
    } catch (e) {
      console.error(e);
      setStatus("❌ Lỗi khi xác minh file.");
    }
  }

  return (
    <div style={{ fontFamily: "Arial", margin: "40px auto", maxWidth: "700px" }}>
      <h1>🏥 Blockchain Document Verifier</h1>
      <button onClick={connectWallet}>
        {account ? `✅ Đã kết nối: ${account.slice(0, 6)}...${account.slice(-4)}` : "🔗 Kết nối MetaMask"}
      </button>

      <hr />
      <h2>🏢 Đăng ký Issuer</h2>
      <input placeholder="Tên" onChange={(e) => setIssuer({ ...issuer, name: e.target.value })} />
      <input placeholder="Tổ chức" onChange={(e) => setIssuer({ ...issuer, organization: e.target.value })} />
      <input placeholder="Email" onChange={(e) => setIssuer({ ...issuer, email: e.target.value })} />
      <button onClick={registerIssuer}>Đăng ký</button>

      <hr />
      <h2>📄 Xác minh tài liệu</h2>
      <input type="file" onChange={(e) => setFile(e.target.files[0])} />
      <div>
        <button onClick={storeHash}>📤 Lưu Hash</button>
        <button onClick={verifyFile} style={{ marginLeft: "10px" }}>
          🔍 Xác minh
        </button>
      </div>

      <pre style={{ background: "#f9f9f9", padding: "10px", marginTop: "20px" }}>{status}</pre>
    </div>
  );
}
