import React, { useState } from "react";
import { ethers } from "ethers";
import verifierAbi from "./abi/DocumentVerifier.json";
import registryAbi from "./abi/IssuerRegistry.json";

// 💾 Cập nhật đúng địa chỉ contract sau khi bạn deploy
const VERIFIER_ADDRESS = "0xCf7Ed3AccA5a467e9e704C703E8D87F634fB0Fc9"; // DocumentVerifier
const REGISTRY_ADDRESS = "0x9fE46736679d2D9a65F0992F2272dE9f3c7fa6e0"; // IssuerRegistry

export default function App() {
  const [account, setAccount] = useState("");
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");
  const [issuerInfo, setIssuerInfo] = useState({
    name: "",
    organization: "",
    email: "",
  });

  // 🦊 Kết nối MetaMask
  async function connectWallet() {
    if (!window.ethereum) {
      alert("⚠️ Hãy cài MetaMask trước!");
      return;
    }
    const provider = new ethers.BrowserProvider(window.ethereum);
    const [addr] = await provider.send("eth_requestAccounts", []);
    setAccount(addr);
  }

  // 🏢 Đăng ký Issuer
  async function registerIssuer() {
    if (!issuerInfo.name || !issuerInfo.organization || !issuerInfo.email) {
      alert("Điền đầy đủ thông tin!");
      return;
    }
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });
      const signer = await provider.getSigner();
      const registry = new ethers.Contract(REGISTRY_ADDRESS, registryAbi, signer);

      const tx = await registry.registerIssuer(
        issuerInfo.name,
        issuerInfo.organization,
        issuerInfo.email
      );
      setStatus("⏳ Đang gửi giao dịch đăng ký...");
      await tx.wait();
      setStatus("✅ Đăng ký Issuer thành công!");
    } catch (err) {
      console.error(err);
      setStatus("❌ Lỗi khi đăng ký Issuer.");
    }
  }

  // 📤 Lưu hash tài liệu
  async function storeHashOnChain() {
    if (!file) return alert("Chưa chọn file để upload!");
    try {
      const buffer = await file.arrayBuffer();
      const hashBytes = new Uint8Array(buffer);
      const hash = ethers.sha256(hashBytes);

      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi, signer);

      const tx = await contract.storeDocument(hash);
      setStatus("⏳ Đang gửi giao dịch lưu hash...");
      await tx.wait();
      setStatus(`✅ Đã lưu hash thành công!`);
    } catch (err) {
      console.error(err);
      setStatus("❌ Lỗi khi lưu hash (kiểm tra đã đăng ký Issuer chưa).");
    }
  }

  // 📁 Xác minh file
  async function verifyFile() {
    if (!file) return alert("Chưa chọn file để xác minh!");
    try {
      const buffer = await file.arrayBuffer();
      const hashBytes = new Uint8Array(buffer);
      const hash = ethers.sha256(hashBytes);

      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi, signer);

      // ⚙️ Contract mới trả về 5 giá trị
      const [exists, issuerAddr, timestamp, issuerName, issuerOrg] =
        await contract.verifyDocument(hash);

      if (!exists) {
        setStatus("❌ File không tồn tại trên blockchain.");
      } else {
        setStatus(
          `✅ File hợp lệ!\nIssuer: ${issuerName || "Chưa đăng ký"} (${
            issuerOrg || "N/A"
          })\nAddress: ${issuerAddr}\nTime: ${new Date(
            Number(timestamp) * 1000
          ).toLocaleString()}`
        );
      }
    } catch (err) {
      console.error(err);
      setStatus("❌ Lỗi khi xác minh file.");
    }
  }

  return (
    <div style={{ fontFamily: "Arial", margin: "40px auto", maxWidth: "700px" }}>
      <h1>🏥 Blockchain Document Verifier</h1>

      {/* Kết nối ví */}
      <button onClick={connectWallet}>
        {account ? `✅ Đã kết nối: ${account.slice(0, 6)}...${account.slice(-4)}` : "🔗 Kết nối MetaMask"}
      </button>

      <hr />

      {/* Đăng ký Issuer */}
      <h2>🏢 Đăng ký Issuer</h2>
      <input
        type="text"
        placeholder="Tên người phát hành"
        onChange={(e) => setIssuerInfo({ ...issuerInfo, name: e.target.value })}
      />
      <input
        type="text"
        placeholder="Tổ chức"
        onChange={(e) => setIssuerInfo({ ...issuerInfo, organization: e.target.value })}
      />
      <input
        type="email"
        placeholder="Email"
        onChange={(e) => setIssuerInfo({ ...issuerInfo, email: e.target.value })}
      />
      <button onClick={registerIssuer}>Đăng ký Issuer</button>

      <hr />

      {/* Xác minh file */}
      <h2>📄 Xác minh tài liệu</h2>
      <input type="file" onChange={(e) => setFile(e.target.files[0])} />
      <div style={{ marginTop: "10px" }}>
        <button onClick={storeHashOnChain}>📤 Lưu Hash</button>
        <button onClick={verifyFile} style={{ marginLeft: "10px" }}>
          🔍 Xác minh
        </button>
      </div>

      <pre style={{ background: "#f0f0f0", padding: "15px", marginTop: "20px" }}>
        {status}
      </pre>
    </div>
  );
}
