import React, { useState } from "react";
import { ethers } from "ethers";
import verifierAbi from "./abi/DocumentVerifier.json";
import registryAbi from "./abi/IssuerRegistry.json";

// üíæ C·∫≠p nh·∫≠t ƒë√∫ng ƒë·ªãa ch·ªâ contract sau khi b·∫°n deploy
const VERIFIER_ADDRESS = "0xCf7Ed3AccA5a467e9e704C703E8D87F634fB0Fc9"; // DocumentVerifier
const REGISTRY_ADDRESS = "0x9fE46736679d2D9a65F0992F2272dE9f3c7fa6e0"; // IssuerRegistry

export default function App() {
  const [account, setAccount] = useState("");
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");
  const [issuerInfo, setIssuerInfo] = useState({
    name: "",
    organization: "",
    email: "",
  });

  // ü¶ä K·∫øt n·ªëi MetaMask
  async function connectWallet() {
    if (!window.ethereum) {
      alert("‚ö†Ô∏è H√£y c√†i MetaMask tr∆∞·ªõc!");
      return;
    }
    const provider = new ethers.BrowserProvider(window.ethereum);
    const [addr] = await provider.send("eth_requestAccounts", []);
    setAccount(addr);
  }

  // üè¢ ƒêƒÉng k√Ω Issuer
  async function registerIssuer() {
    if (!issuerInfo.name || !issuerInfo.organization || !issuerInfo.email) {
      alert("ƒêi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin!");
      return;
    }
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });
      const signer = await provider.getSigner();
      const registry = new ethers.Contract(REGISTRY_ADDRESS, registryAbi, signer);

      const tx = await registry.registerIssuer(
        issuerInfo.name,
        issuerInfo.organization,
        issuerInfo.email
      );
      setStatus("‚è≥ ƒêang g·ª≠i giao d·ªãch ƒëƒÉng k√Ω...");
      await tx.wait();
      setStatus("‚úÖ ƒêƒÉng k√Ω Issuer th√†nh c√¥ng!");
    } catch (err) {
      console.error(err);
      setStatus("‚ùå L·ªói khi ƒëƒÉng k√Ω Issuer.");
    }
  }

  // üì§ L∆∞u hash t√†i li·ªáu
  async function storeHashOnChain() {
    if (!file) return alert("Ch∆∞a ch·ªçn file ƒë·ªÉ upload!");
    try {
      const buffer = await file.arrayBuffer();
      const hashBytes = new Uint8Array(buffer);
      const hash = ethers.sha256(hashBytes);

      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi, signer);

      const tx = await contract.storeDocument(hash);
      setStatus("‚è≥ ƒêang g·ª≠i giao d·ªãch l∆∞u hash...");
      await tx.wait();
      setStatus(`‚úÖ ƒê√£ l∆∞u hash th√†nh c√¥ng!`);
    } catch (err) {
      console.error(err);
      setStatus("‚ùå L·ªói khi l∆∞u hash (ki·ªÉm tra ƒë√£ ƒëƒÉng k√Ω Issuer ch∆∞a).");
    }
  }

  // üìÅ X√°c minh file
  async function verifyFile() {
    if (!file) return alert("Ch∆∞a ch·ªçn file ƒë·ªÉ x√°c minh!");
    try {
      const buffer = await file.arrayBuffer();
      const hashBytes = new Uint8Array(buffer);
      const hash = ethers.sha256(hashBytes);

      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi, signer);

      // ‚öôÔ∏è Contract m·ªõi tr·∫£ v·ªÅ 5 gi√° tr·ªã
      const [exists, issuerAddr, timestamp, issuerName, issuerOrg] =
        await contract.verifyDocument(hash);

      if (!exists) {
        setStatus("‚ùå File kh√¥ng t·ªìn t·∫°i tr√™n blockchain.");
      } else {
        setStatus(
          `‚úÖ File h·ª£p l·ªá!\nIssuer: ${issuerName || "Ch∆∞a ƒëƒÉng k√Ω"} (${
            issuerOrg || "N/A"
          })\nAddress: ${issuerAddr}\nTime: ${new Date(
            Number(timestamp) * 1000
          ).toLocaleString()}`
        );
      }
    } catch (err) {
      console.error(err);
      setStatus("‚ùå L·ªói khi x√°c minh file.");
    }
  }

  return (
    <div style={{ fontFamily: "Arial", margin: "40px auto", maxWidth: "700px" }}>
      <h1>üè• Blockchain Document Verifier</h1>

      {/* K·∫øt n·ªëi v√≠ */}
      <button onClick={connectWallet}>
        {account ? `‚úÖ ƒê√£ k·∫øt n·ªëi: ${account.slice(0, 6)}...${account.slice(-4)}` : "üîó K·∫øt n·ªëi MetaMask"}
      </button>

      <hr />

      {/* ƒêƒÉng k√Ω Issuer */}
      <h2>üè¢ ƒêƒÉng k√Ω Issuer</h2>
      <input
        type="text"
        placeholder="T√™n ng∆∞·ªùi ph√°t h√†nh"
        onChange={(e) => setIssuerInfo({ ...issuerInfo, name: e.target.value })}
      />
      <input
        type="text"
        placeholder="T·ªï ch·ª©c"
        onChange={(e) => setIssuerInfo({ ...issuerInfo, organization: e.target.value })}
      />
      <input
        type="email"
        placeholder="Email"
        onChange={(e) => setIssuerInfo({ ...issuerInfo, email: e.target.value })}
      />
      <button onClick={registerIssuer}>ƒêƒÉng k√Ω Issuer</button>

      <hr />

      {/* X√°c minh file */}
      <h2>üìÑ X√°c minh t√†i li·ªáu</h2>
      <input type="file" onChange={(e) => setFile(e.target.files[0])} />
      <div style={{ marginTop: "10px" }}>
        <button onClick={storeHashOnChain}>üì§ L∆∞u Hash</button>
        <button onClick={verifyFile} style={{ marginLeft: "10px" }}>
          üîç X√°c minh
        </button>
      </div>

      <pre style={{ background: "#f0f0f0", padding: "15px", marginTop: "20px" }}>
        {status}
      </pre>
    </div>
  );
}
