import { useState, useEffect } from "react";
import { ethers } from "ethers";
import verifierAbi from "./abi/DocumentVerifier.json";
import registryAbi from "./abi/IssuerRegistry.json";
import "./App.css";

const VERIFIER_ADDRESS = "0xCf7Ed3AccA5a467e9e704C703E8D87F634fB0Fc9";
const REGISTRY_ADDRESS = "0x9fE46736679d2D9a65F0992F2272dE9f3c7fa6e0";

function App() {
  const [account, setAccount] = useState("");
  const [tab, setTab] = useState("verifier");
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");
  const [history, setHistory] = useState([]);
  const [issuerName, setIssuerName] = useState("");
  const [issuerOrg, setIssuerOrg] = useState("");
  const [issuerEmail, setIssuerEmail] = useState("");
  const [regStatus, setRegStatus] = useState("");

  useEffect(() => {
    if (window.ethereum) {
      window.ethereum.request({ method: "eth_accounts" }).then((a) => {
        if (a && a.length) setAccount(a[0]);
      });
    }
  }, []);

  async function connectWallet() {
    if (!window.ethereum) return alert("‚ö†Ô∏è C√†i MetaMask tr∆∞·ªõc ƒë√£!");
    const [addr] = await window.ethereum.request({
      method: "eth_requestAccounts",
    });
    setAccount(addr);
  }

  function handleFile(e) {
    const f = e.target.files[0];
    setFile(f || null);
    setStatus("");
  }

  async function storeHashOnChain() {
    if (!file) return alert("Ch·ªçn file tr∆∞·ªõc!");
    try {
      setStatus("üîÑ T√≠nh SHA256...");
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      const provider = new ethers.JsonRpcProvider("http://127.0.0.1:8545", {
        name: "hardhat",
        chainId: 31337,
      });
      const signer = await provider.getSigner(account);

      const contract = new ethers.Contract(
        VERIFIER_ADDRESS,
        verifierAbi.abi,
        signer
      );

      setStatus("‚õìÔ∏è G·ª≠i giao d·ªãch l∆∞u hash...");
      const tx = await contract.storeDocument(hash);
      await tx.wait();
      setStatus(`‚úÖ L∆∞u th√†nh c√¥ng! Hash: ${hash}`);
      setHistory((h) => [
        { hash, fileName: file.name, time: new Date().toLocaleString() },
        ...h,
      ]);
    } catch (err) {
      console.error(err);
      setStatus("‚ùå L·ªói: " + (err.reason || err.message));
    }
  }

  async function verifyFile() {
    if (!file) return alert("Ch·ªçn file tr∆∞·ªõc!");
    try {
      setStatus("üîç T√≠nh SHA256...");
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      const provider = new ethers.JsonRpcProvider("http://127.0.0.1:8545", {
        name: "hardhat",
        chainId: 31337,
      });

      const contract = new ethers.Contract(
        VERIFIER_ADDRESS,
        verifierAbi.abi,
        provider
      );

      setStatus("‚õìÔ∏è Ki·ªÉm tra tr√™n blockchain...");
      const [exists, issuerAddr, timestamp, name, org] =
        await contract.verifyDocument(hash);

      if (!exists) setStatus("‚ùå File ch∆∞a ƒë∆∞·ª£c l∆∞u tr√™n blockchain.");
      else
        setStatus(
          `‚úÖ File c√≥ tr√™n chain!\nIssuer: ${name} (${org})\nAddress: ${issuerAddr}\nTime: ${new Date(
            Number(timestamp) * 1000
          ).toLocaleString()}`
        );
    } catch (err) {
      console.error(err);
      setStatus("‚ùå L·ªói: " + (err.reason || err.message));
    }
  }

  async function registerIssuer() {
    if (!issuerName || !issuerOrg || !issuerEmail)
      return alert("Nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin!");
    try {
      setRegStatus("‚õìÔ∏è G·ª≠i giao d·ªãch ƒëƒÉng k√Ω...");
      const provider = new ethers.JsonRpcProvider("http://127.0.0.1:8545", {
        name: "hardhat",
        chainId: 31337,
      });
      const signer = await provider.getSigner(account);

      const registry = new ethers.Contract(
        REGISTRY_ADDRESS,
        registryAbi.abi,
        signer
      );

      const tx = await registry.registerIssuer(
        issuerName,
        issuerOrg,
        issuerEmail
      );
      await tx.wait();
      setRegStatus("‚úÖ ƒêƒÉng k√Ω th√†nh c√¥ng!");
    } catch (err) {
      console.error(err);
      setRegStatus("‚ùå L·ªói: " + (err.reason || err.message));
    }
  }

  return (
    <div className="app">
      <header className="topbar">
        <h1>üìú Document Verifier PRO</h1>
        <div>
          {account ? (
            <div className="wallet">{account}</div>
          ) : (
            <button onClick={connectWallet} className="btn">
              K·∫øt n·ªëi MetaMask
            </button>
          )}
        </div>
      </header>

      <div className="tabs">
        <button
          className={`tab ${tab === "verifier" ? "active" : ""}`}
          onClick={() => setTab("verifier")}
        >
          üßæ X√°c minh t√†i li·ªáu
        </button>
        <button
          className={`tab ${tab === "issuer" ? "active" : ""}`}
          onClick={() => setTab("issuer")}
        >
          üè¢ ƒêƒÉng k√Ω Issuer
        </button>
      </div>

      {tab === "verifier" ? (
        <main className="container">
          <section className="card">
            <h2>T·∫£i l√™n v√† l∆∞u Hash</h2>
            <input type="file" onChange={handleFile} />
            {file && (
              <div className="preview">
                {file.type.startsWith("image/") ? (
                  <img
                    src={URL.createObjectURL(file)}
                    alt="preview"
                    className="preview-img"
                  />
                ) : (
                  <div>{file.name}</div>
                )}
              </div>
            )}
            <div className="actions">
              <button onClick={storeHashOnChain} className="btn">
                L∆∞u Hash
              </button>
              <button onClick={verifyFile} className="btn">
                X√°c minh
              </button>
            </div>
            <pre className="status">{status || "Ch∆∞a c√≥ ho·∫°t ƒë·ªông"}</pre>
          </section>

          <aside className="card">
            <h3>L·ªãch s·ª≠ (local)</h3>
            {history.length === 0 ? (
              <div className="muted">Ch∆∞a c√≥ d·ªØ li·ªáu</div>
            ) : (
              history.map((h, i) => (
                <div key={i} className="histItem">
                  <div className="meta">{h.fileName}</div>
                  <div className="hash">{h.hash}</div>
                  <div className="time">{h.time}</div>
                </div>
              ))
            )}
          </aside>
        </main>
      ) : (
        <main className="container">
          <section className="card">
            <h2>ƒêƒÉng k√Ω Issuer</h2>
            <input
              placeholder="T√™n ng∆∞·ªùi ph√°t h√†nh"
              value={issuerName}
              onChange={(e) => setIssuerName(e.target.value)}
            />
            <input
              placeholder="T√™n t·ªï ch·ª©c"
              value={issuerOrg}
              onChange={(e) => setIssuerOrg(e.target.value)}
            />
            <input
              placeholder="Email"
              value={issuerEmail}
              onChange={(e) => setIssuerEmail(e.target.value)}
            />
            <button onClick={registerIssuer} className="btn">
              ƒêƒÉng k√Ω
            </button>
            <pre className="status">{regStatus || "Ch∆∞a th·ª±c hi·ªán"}</pre>
          </section>
        </main>
      )}

      <footer className="footer">
        ¬© 2025 ‚Äî Document Verifier PRO ‚Äî Blockchain-based authenticity
      </footer>
    </div>
  );
}

export default App;
