import React, { useState } from "react";
import { ethers } from "ethers";
import verifierAbi from "./abi/DocumentVerifier.json";
import registryAbi from "./abi/IssuerRegistry.json";

const VERIFIER_ADDRESS = "0xe7f1725E7734CE288F8367e1Bb143E90bb3F0512";
const REGISTRY_ADDRESS = "0x5FbDB2315678afecb367f032d93F642f64180aa3";

export default function App() {
  const [account, setAccount] = useState("");
  const [issuer, setIssuer] = useState({ name: "", organization: "", email: "" });
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  // üéØ T·∫°o provider chu·∫©n cho localhost
  const getProvider = async () => {
    const provider = new ethers.BrowserProvider(window.ethereum);
    provider.getNetwork = async () => ({
      name: "hardhat",
      chainId: 31337,
      ensAddress: null,
    });
    return provider;
  };

  // üîó K·∫øt n·ªëi v√≠ MetaMask
  async function connectWallet() {
    if (!window.ethereum) return alert("‚ö†Ô∏è C√†i MetaMask tr∆∞·ªõc!");
    const provider = await getProvider();
    const [addr] = await provider.send("eth_requestAccounts", []);
    setAccount(addr);
    setStatus("‚úÖ ƒê√£ k·∫øt n·ªëi MetaMask.");
  }

  // üè¢ ƒêƒÉng k√Ω Issuer
  async function registerIssuer() {
    try {
      setIsLoading(true);
      const provider = await getProvider();
      const signer = await provider.getSigner();
      const registry = new ethers.Contract(REGISTRY_ADDRESS, registryAbi.abi, signer);

      const tx = await registry.registerIssuer(issuer.name, issuer.organization, issuer.email);
      setStatus("‚è≥ ƒêang ƒëƒÉng k√Ω...");
      await tx.wait();

      setStatus("‚úÖ ƒêƒÉng k√Ω Issuer th√†nh c√¥ng!");
    } catch (err) {
      console.error("Register error:", err);
      if (err.message.includes("revert")) {
        setStatus("‚ùå B·∫°n ƒë√£ ƒëƒÉng k√Ω Issuer tr∆∞·ªõc ƒë√≥.");
      } else {
        setStatus("‚ùå L·ªói khi ƒëƒÉng k√Ω Issuer. Ki·ªÉm tra m·∫°ng ho·∫∑c ƒë·ªãa ch·ªâ contract.");
      }
    } finally {
      setIsLoading(false);
    }
  }

  // üìÑ L∆∞u hash t√†i li·ªáu
  async function storeHash() {
    if (!file) return alert("Ch∆∞a ch·ªçn file!");
    try {
      setIsLoading(true);
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      const provider = await getProvider();
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi.abi, signer);

      const tx = await contract.storeDocument(hash);
      setStatus("‚è≥ ƒêang l∆∞u hash...");
      await tx.wait();

      setStatus("‚úÖ Hash ƒë√£ ƒë∆∞·ª£c l∆∞u th√†nh c√¥ng l√™n blockchain!");
    } catch (err) {
      console.error("Store hash error:", err);

      if (err.message.includes("Already stored")) {
        setStatus("‚ö†Ô∏è Hash n√†y ƒë√£ t·ªìn t·∫°i tr√™n blockchain. File ƒë√£ ƒë∆∞·ª£c l∆∞u tr∆∞·ªõc ƒë√≥.");
      } else if (err.message.includes("Issuer not registered")) {
        setStatus("‚ùå B·∫°n ch∆∞a ƒëƒÉng k√Ω Issuer. H√£y ƒëƒÉng k√Ω tr∆∞·ªõc khi l∆∞u hash.");
      } else {
        setStatus("‚ùå L·ªói khi l∆∞u hash (ki·ªÉm tra k·∫øt n·ªëi blockchain).");
      }
    } finally {
      setIsLoading(false);
    }
  }

  // üîç X√°c minh t√†i li·ªáu
  async function verifyFile() {
    if (!file) return alert("Ch∆∞a ch·ªçn file!");
    try {
      setIsLoading(true);
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      const provider = await getProvider();
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi.abi, signer);

      const [exists, issuerAddr, timestamp, name, org] = await contract.verifyDocument(hash);
      if (!exists) return setStatus("‚ùå File ch∆∞a ƒë∆∞·ª£c l∆∞u tr√™n blockchain.");

      setStatus(
        `‚úÖ File h·ª£p l·ªá!\nIssuer: ${name} (${org})\nAddress: ${issuerAddr}\nTime: ${new Date(
          Number(timestamp) * 1000
        ).toLocaleString()}`
      );
    } catch (err) {
      console.error("Verify error:", err);
      setStatus("‚ùå L·ªói khi x√°c minh file.");
    } finally {
      setIsLoading(false);
    }
  }

  // üé® Giao di·ªán + hi·ªáu ·ª©ng
  return (
    <div style={styles.container}>
      <h1 style={styles.title}>üìú Blockchain Document Verifier</h1>

      <button onClick={connectWallet} style={styles.connectBtn}>
        {account
          ? `‚úÖ ƒê√£ k·∫øt n·ªëi: ${account.slice(0, 6)}...${account.slice(-4)}`
          : "üîó K·∫øt n·ªëi MetaMask"}
      </button>

      <section style={styles.section}>
        <h2 style={styles.subtitle}>üè¢ ƒêƒÉng k√Ω Issuer</h2>
        <input
          style={styles.input}
          placeholder="T√™n"
          onChange={(e) => setIssuer({ ...issuer, name: e.target.value })}
        />
        <input
          style={styles.input}
          placeholder="T·ªï ch·ª©c"
          onChange={(e) => setIssuer({ ...issuer, organization: e.target.value })}
        />
        <input
          style={styles.input}
          placeholder="Email"
          onChange={(e) => setIssuer({ ...issuer, email: e.target.value })}
        />
        <button onClick={registerIssuer} style={styles.actionBtn} disabled={isLoading}>
          {isLoading ? "‚è≥ ƒêang x·ª≠ l√Ω..." : "üìù ƒêƒÉng k√Ω"}
        </button>
      </section>

      <section style={styles.section}>
        <h2 style={styles.subtitle}>üìÅ X√°c minh t√†i li·ªáu</h2>
        <input type="file" onChange={(e) => setFile(e.target.files[0])} style={styles.fileInput} />
        <div>
          <button onClick={storeHash} style={styles.actionBtn} disabled={isLoading}>
            üì§ L∆∞u Hash
          </button>
          <button
            onClick={verifyFile}
            style={{ ...styles.actionBtn, background: "#0d6efd" }}
            disabled={isLoading}
          >
            üîç X√°c minh
          </button>
        </div>
      </section>

      <div
        style={{
          ...styles.statusBox,
          background: status.includes("‚ùå")
            ? "#ffe3e3"
            : status.includes("‚ö†Ô∏è")
            ? "#fff8e1"
            : "#e3ffe9",
          color: status.includes("‚ùå")
            ? "#b71c1c"
            : status.includes("‚ö†Ô∏è")
            ? "#795548"
            : "#1b5e20",
        }}
      >
        {status}
      </div>
    </div>
  );
}

// üåà CSS inline
const styles = {
  container: {
    maxWidth: "700px",
    margin: "50px auto",
    fontFamily: "Segoe UI, sans-serif",
    background: "#f9f9fb",
    padding: "30px 40px",
    borderRadius: "16px",
    boxShadow: "0 0 20px rgba(0,0,0,0.1)",
    animation: "fadeIn 0.8s ease-in-out",
  },
  title: {
    textAlign: "center",
    fontSize: "28px",
    color: "#222",
    marginBottom: "20px",
  },
  connectBtn: {
    display: "block",
    margin: "0 auto 30px",
    background: "#28a745",
    color: "white",
    padding: "10px 20px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    fontSize: "15px",
    transition: "all 0.3s ease",
  },
  section: {
    marginBottom: "30px",
  },
  subtitle: {
    fontSize: "20px",
    marginBottom: "10px",
    color: "#333",
  },
  input: {
    display: "block",
    width: "100%",
    padding: "8px 10px",
    marginBottom: "10px",
    border: "1px solid #ccc",
    borderRadius: "6px",
  },
  actionBtn: {
    background: "#17a2b8",
    color: "white",
    border: "none",
    borderRadius: "6px",
    padding: "8px 14px",
    cursor: "pointer",
    marginTop: "5px",
    marginRight: "8px",
    transition: "all 0.3s",
  },
  fileInput: {
    marginBottom: "10px",
  },
  statusBox: {
    whiteSpace: "pre-line",
    padding: "15px",
    borderRadius: "10px",
    marginTop: "20px",
    fontSize: "15px",
    transition: "0.3s",
  },
};
