import { useEffect, useState } from "react";
import { ethers } from "ethers";
import abi from "./abi/DocumentVerifier.json";
import "./App.css";

const CONTRACT_ADDRESS = "0x5FbDB2315678afecb367f032d93F642f64180aa3";

function App() {
  const [account, setAccount] = useState("");
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");
  const [history, setHistory] = useState([]);
  const [busy, setBusy] = useState(false);

  useEffect(() => {
    if (window.ethereum) {
      window.ethereum.request({ method: "eth_accounts" })
        .then(accounts => accounts.length && setAccount(accounts[0]));

      // Listen for account changes
      window.ethereum.on('accountsChanged', (accounts) => {
        setAccount(accounts[0] || "");
      });
    }
  }, []);

  async function connectWallet() {
    if (!window.ethereum) {
      return alert("‚ö†Ô∏è Please install MetaMask to use this app!");
    }
    try {
      const [addr] = await window.ethereum.request({ method: "eth_requestAccounts" });
      setAccount(addr);
      setStatus("‚úÖ Wallet connected successfully!");
    } catch (error) {
      setStatus("‚ùå Failed to connect wallet: " + error.message);
    }
  }

  async function storeHashOnChain() {
    if (!file) return alert("‚ö†Ô∏è Please select a file first!");
    if (!account) return alert("‚ö†Ô∏è Please connect your wallet first!");

    try {
      setBusy(true);
      setStatus("üîÑ Computing SHA256 hash...");

      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      setStatus("üì§ Sending transaction to blockchain...");
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(CONTRACT_ADDRESS, abi.abi, signer);

      const tx = await contract.storeDocument(hash);
      setStatus("‚è≥ Waiting for confirmation...");

      await tx.wait();

      setStatus(`‚úÖ Success! Document hash saved on blockchain.\n\nüìù Hash: ${hash}\n‚õìÔ∏è Transaction: ${tx.hash}`);
      setHistory(h => [{ 
        hash, 
        fileName: file.name, 
        time: Date.now(),
        txHash: tx.hash 
      }, ...h]);
    } catch (e) {
      console.error(e);
      setStatus("‚ùå Error: " + (e?.reason || e?.message || e));
    } finally {
      setBusy(false);
    }
  }

  async function verifyFile() {
    if (!file) return alert("‚ö†Ô∏è Please select a file first!");

    try {
      setBusy(true);
      setStatus("üîÑ Computing SHA256 hash...");

      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      setStatus("üîç Verifying on blockchain...");
      const provider = new ethers.BrowserProvider(window.ethereum);
      const contract = new ethers.Contract(CONTRACT_ADDRESS, abi.abi, provider);

      const [exists, issuer, timestamp] = await contract.verifyDocument(hash);

      if (!exists) {
        setStatus(`‚ùå Document not found on blockchain.\n\nüìù Hash: ${hash}\n\nThis document has not been registered yet.`);
      } else {
        const date = new Date(Number(timestamp) * 1000);
        setStatus(`‚úÖ Document verified successfully!\n\nüìù Hash: ${hash}\nüë§ Issuer: ${issuer}\nüìÖ Registered: ${date.toLocaleString()}`);
      }
    } catch (e) {
      console.error(e);
      setStatus("‚ùå Error: " + (e?.reason || e?.message || e));
    } finally {
      setBusy(false);
    }
  }

  function formatAddress(addr) {
    return `${addr.slice(0, 6)}...${addr.slice(-4)}`;
  }

  return (
    <div className="app">
      <header className="topbar">
        <h1>Document Verifier</h1>
        <div>
          {account ? (
            <div className="wallet" title={account}>
              {formatAddress(account)}
            </div>
          ) : (
            <button onClick={connectWallet} className="btn">
              Connect Wallet
            </button>
          )}
        </div>
      </header>

      <main className="container">
        <section className="card">
          <h2>Upload & Verify Document</h2>

          <input 
            type="file" 
            onChange={e => setFile(e.target.files[0])}
            accept="*/*"
          />

          {file && (
            <div className="preview">
              {file.type.startsWith("image/") ? (
                <img src={URL.createObjectURL(file)} alt="preview" />
              ) : (
                <div>{file.name}</div>
              )}
            </div>
          )}

          <div className="actions">
            <button 
              onClick={storeHashOnChain} 
              className="btn" 
              disabled={busy || !account}
              title={!account ? "Connect wallet first" : "Save document hash to blockchain"}
            >
              {busy ? "‚è≥ Processing..." : "üíæ Save to Blockchain"}
            </button>
            <button 
              onClick={verifyFile} 
              className="btn" 
              disabled={busy}
              title="Verify if document exists on blockchain"
            >
              {busy ? "üîç Checking..." : "‚úì Verify Document"}
            </button>
          </div>

          {status ? (
            <pre className="status">{status}</pre>
          ) : (
            <pre className="status">üí° Upload a document to get started.\n\nYou can save document hashes to the blockchain or verify existing documents.</pre>
          )}
        </section>

        <aside className="card">
          <h3>Recent Activity</h3>
          {history.length === 0 ? (
            <div className="muted">No documents stored yet.<br/>Your activity will appear here.</div>
          ) : (
            history.map((h, i) => (
              <div key={i} className="histItem">
                <div className="meta">{h.fileName}</div>
                <div className="hash">{h.hash}</div>
              </div>
            ))
          )}
        </aside>
      </main>

      <footer className="footer">
        üîê Powered by Ethereum Smart Contracts | Document integrity you can trust
      </footer>
    </div>
  );
}

export default App;
