import { useState, useEffect } from "react";
import { ethers } from "ethers";
import verifierAbi from "./abi/DocumentVerifier.json";
import registryAbi from "./abi/IssuerRegistry.json";
import "./App.css";

// 🔁 Thay bằng địa chỉ thật khi bạn deploy
const VERIFIER_ADDRESS = "0xYourVerifierAddressHere";
const REGISTRY_ADDRESS = "0xYourRegistryAddressHere";

function App() {
  const [account, setAccount] = useState("");
  const [tab, setTab] = useState("verifier");

  // ====== DOCUMENT VERIFIER STATE ======
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");
  const [history, setHistory] = useState([]);

  // ====== ISSUER REGISTRATION STATE ======
  const [issuerName, setIssuerName] = useState("");
  const [issuerOrg, setIssuerOrg] = useState("");
  const [issuerEmail, setIssuerEmail] = useState("");
  const [regStatus, setRegStatus] = useState("");

  // 🦊 Auto-connect MetaMask nếu đã mở
  useEffect(() => {
    if (window.ethereum) {
      window.ethereum.request({ method: "eth_accounts" }).then((a) => {
        if (a && a.length) setAccount(a[0]);
      });
    }
  }, []);

  async function connectWallet() {
    if (!window.ethereum) return alert("⚠️ Cài MetaMask trước đã!");
    const [addr] = await window.ethereum.request({
      method: "eth_requestAccounts",
    });
    setAccount(addr);
  }

  // ====== FILE ======
  function handleFile(e) {
    const f = e.target.files[0];
    setFile(f || null);
    setStatus("");
  }

  // ====== STORE HASH ONCHAIN ======
  async function storeHashOnChain() {
    if (!file) return alert("Chọn file trước!");
    try {
      setStatus("🔄 Tính SHA256...");
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      const provider = new ethers.BrowserProvider(window.ethereum);
      // ⚙️ FIX ENS cho mạng localhost
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });

      const signer = await provider.getSigner(account);

      const contract = new ethers.Contract(
        VERIFIER_ADDRESS,
        verifierAbi.abi,
        signer
      );

      setStatus("⛓️ Gửi giao dịch lưu hash lên blockchain...");
      const tx = await contract.storeDocument(hash);
      await tx.wait();

      setStatus(`✅ Lưu thành công! Hash: ${hash}`);
      setHistory((h) => [
        { hash, fileName: file.name, time: new Date().toLocaleString() },
        ...h,
      ]);
    } catch (err) {
      console.error(err);
      setStatus("❌ Lỗi: " + (err.reason || err.message));
    }
  }

  // ====== VERIFY FILE ======
  async function verifyFile() {
    if (!file) return alert("Chọn file trước!");
    try {
      setStatus("🔍 Tính SHA256...");
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      const provider = new ethers.BrowserProvider(window.ethereum);
      // ⚙️ FIX ENS cho mạng localhost
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });

      const contract = new ethers.Contract(
        VERIFIER_ADDRESS,
        verifierAbi.abi,
        provider
      );

      setStatus("⛓️ Kiểm tra trên blockchain...");
      const [exists, issuerAddr, timestamp, name, org] =
        await contract.verifyDocument(hash);

      if (!exists) setStatus("❌ File chưa được lưu trên blockchain.");
      else
        setStatus(
          `✅ File có trên chain!\nIssuer: ${name} (${org})\nAddress: ${issuerAddr}\nTime: ${new Date(
            Number(timestamp) * 1000
          ).toLocaleString()}`
        );
    } catch (err) {
      console.error(err);
      setStatus("❌ Lỗi: " + (err.reason || err.message));
    }
  }

  // ====== REGISTER ISSUER ======
  async function registerIssuer() {
    if (!issuerName || !issuerOrg || !issuerEmail)
      return alert("Nhập đầy đủ thông tin!");

    try {
      setRegStatus("⛓️ Gửi giao dịch đăng ký...");
      const provider = new ethers.BrowserProvider(window.ethereum);
      // ⚙️ FIX ENS cho mạng localhost
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });

      const signer = await provider.getSigner(account);

      const registry = new ethers.Contract(
        REGISTRY_ADDRESS,
        registryAbi.abi,
        signer
      );

      const tx = await registry.registerIssuer(
        issuerName,
        issuerOrg,
        issuerEmail
      );
      await tx.wait();
      setRegStatus("✅ Đăng ký thành công!");
    } catch (err) {
      console.error(err);
      setRegStatus("❌ Lỗi: " + (err.reason || err.message));
    }
  }

  // ====== UI ======
  return (
    <div className="app">
      <header className="topbar">
        <h1>📜 Document Verifier PRO</h1>
        <div>
          {account ? (
            <div className="wallet">{account}</div>
          ) : (
            <button onClick={connectWallet} className="btn">
              Kết nối MetaMask
            </button>
          )}
        </div>
      </header>

      <div className="tabs">
        <button
          className={`tab ${tab === "verifier" ? "active" : ""}`}
          onClick={() => setTab("verifier")}
        >
          🧾 Xác minh tài liệu
        </button>
        <button
          className={`tab ${tab === "issuer" ? "active" : ""}`}
          onClick={() => setTab("issuer")}
        >
          🏢 Đăng ký Issuer
        </button>
      </div>

      {tab === "verifier" ? (
        <main className="container">
          <section className="card">
            <h2>Tải lên và lưu Hash</h2>
            <input type="file" onChange={handleFile} />
            {file && (
              <div className="preview">
                {file.type.startsWith("image/") ? (
                  <img
                    src={URL.createObjectURL(file)}
                    alt="preview"
                    className="preview-img"
                  />
                ) : (
                  <div>{file.name}</div>
                )}
              </div>
            )}
            <div className="actions">
              <button onClick={storeHashOnChain} className="btn">
                Lưu Hash
              </button>
              <button onClick={verifyFile} className="btn">
                Xác minh
              </button>
            </div>
            <pre className="status">{status || "Chưa có hoạt động"}</pre>
          </section>

          <aside className="card">
            <h3>Lịch sử (local)</h3>
            {history.length === 0 ? (
              <div className="muted">Chưa có dữ liệu</div>
            ) : (
              history.map((h, i) => (
                <div key={i} className="histItem">
                  <div className="meta">{h.fileName}</div>
                  <div className="hash">{h.hash}</div>
                  <div className="time">{h.time}</div>
                </div>
              ))
            )}
          </aside>
        </main>
      ) : (
        <main className="container">
          <section className="card">
            <h2>Đăng ký Issuer</h2>
            <input
              placeholder="Tên người phát hành"
              value={issuerName}
              onChange={(e) => setIssuerName(e.target.value)}
            />
            <input
              placeholder="Tên tổ chức"
              value={issuerOrg}
              onChange={(e) => setIssuerOrg(e.target.value)}
            />
            <input
              placeholder="Email"
              value={issuerEmail}
              onChange={(e) => setIssuerEmail(e.target.value)}
            />
            <button onClick={registerIssuer} className="btn">
              Đăng ký
            </button>
            <pre className="status">{regStatus || "Chưa thực hiện"}</pre>
          </section>
        </main>
      )}

      <footer className="footer">
        © 2025 — Document Verifier PRO — Blockchain-based authenticity
      </footer>
    </div>
  );
}

export default App;
