import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import verifierAbi from "./abi/DocumentVerifier.json";
import registryAbi from "./abi/IssuerRegistry.json";
import "./App.css";

const VERIFIER_ADDRESS = "0xYourDocumentVerifierAddress";
const REGISTRY_ADDRESS = "0xYourIssuerRegistryAddress";

export default function App() {
  const [account, setAccount] = useState("");
  const [issuer, setIssuer] = useState({ name: "", organization: "", email: "" });
  const [issuersList, setIssuersList] = useState([]);
  const [selectedIssuer, setSelectedIssuer] = useState("");
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");
  const [loading, setLoading] = useState(false);
  const [history, setHistory] = useState([]);
  const [errorLogs, setErrorLogs] = useState([]);

  const addHistory = (msg, type = "info") => {
    const time = new Date().toLocaleString();
    setHistory((prev) => [{ time, msg, type }, ...prev]);
  };

  const addError = (location, err, suggestion = "") => {
    const time = new Date().toLocaleString();
    const errMsg = err?.reason || err?.message || String(err);
    const details = {
      time,
      location,
      message: errMsg,
      suggestion: suggestion || "Xem console ƒë·ªÉ bi·∫øt chi ti·∫øt th√™m.",
    };
    console.error(`‚ùå [${location}]`, err);
    setErrorLogs((prev) => [details, ...prev]);
    addHistory(`‚ùå ${location}: ${errMsg}`, "error");
    setStatus(`‚ùå L·ªói t·∫°i ${location}: ${errMsg}`);
  };

  const getProvider = async () => {
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({ name: "hardhat", chainId: 31337, ensAddress: null });
      return provider;
    } catch (err) {
      addError("getProvider", err, "Ki·ªÉm tra MetaMask ho·∫∑c Hardhat node.");
      throw err;
    }
  };

  async function connectWallet() {
    try {
      if (!window.ethereum) throw new Error("MetaMask ch∆∞a ƒë∆∞·ª£c c√†i!");
      const provider = await getProvider();
      const [addr] = await provider.send("eth_requestAccounts", []);
      setAccount(addr);
      addHistory(`üîó K·∫øt n·ªëi MetaMask th√†nh c√¥ng: ${addr}`);
    } catch (err) {
      addError("connectWallet", err, "H√£y m·ªü MetaMask v√† th·ª≠ l·∫°i.");
    }
  }

  async function registerIssuer() {
    try {
      setLoading(true);
      const provider = await getProvider();
      const signer = await provider.getSigner();
      const registry = new ethers.Contract(REGISTRY_ADDRESS, registryAbi.abi, signer);

      if (!issuer.name || !issuer.organization || !issuer.email)
        throw new Error("Thi·∫øu th√¥ng tin Issuer.");

      const tx = await registry.registerIssuer(issuer.name, issuer.organization, issuer.email);
      setStatus("‚è≥ ƒêang g·ª≠i giao d·ªãch ƒëƒÉng k√Ω...");
      await tx.wait();
      setStatus("‚úÖ ƒêƒÉng k√Ω Issuer th√†nh c√¥ng!");
      addHistory(`ƒêƒÉng k√Ω Issuer: ${issuer.name} (${issuer.organization})`);
      await loadIssuers();
    } catch (err) {
      if (err.reason?.includes("Empty name")) {
        addError("registerIssuer", err, "T√™n Issuer kh√¥ng ƒë∆∞·ª£c ƒë·ªÉ tr·ªëng.");
      } else {
        addError("registerIssuer", err);
      }
    } finally {
      setLoading(false);
    }
  }

  async function loadIssuers() {
    try {
      const provider = await getProvider();
      const registry = new ethers.Contract(REGISTRY_ADDRESS, registryAbi.abi, provider);
      const issuers = await registry.getAllIssuers();

      const formatted = issuers.map((i) => ({
        id: i.id.toString(),
        name: i.name,
        org: i.organization,
        email: i.email,
        owner: i.owner,
      }));
      setIssuersList(formatted);
    } catch (err) {
      addError("loadIssuers", err, "Ki·ªÉm tra ABI v√† contract address.");
    }
  }

  useEffect(() => {
    loadIssuers();
  }, []);

  async function storeHash() {
    if (!file) return alert("Ch∆∞a ch·ªçn file!");
    if (!selectedIssuer) return alert("Ch∆∞a ch·ªçn Issuer!");

    try {
      setLoading(true);
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      const provider = await getProvider();
      const signer = await provider.getSigner();
      const verifier = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi.abi, signer);

      const tx = await verifier.storeDocument(hash, Number(selectedIssuer));
      setStatus("‚è≥ ƒêang l∆∞u hash...");
      await tx.wait();
      setStatus("‚úÖ L∆∞u hash th√†nh c√¥ng!");
      addHistory(`L∆∞u hash: ${file.name}`);
    } catch (err) {
      if (err.reason?.includes("Already stored"))
        addError("storeHash", err, "T√†i li·ªáu n√†y ƒë√£ ƒë∆∞·ª£c l∆∞u tr∆∞·ªõc ƒë√≥.");
      else addError("storeHash", err);
    } finally {
      setLoading(false);
    }
  }

  async function verifyFile() {
    if (!file) return alert("Ch∆∞a ch·ªçn file!");
    try {
      setLoading(true);
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));
      const provider = await getProvider();
      const verifier = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi.abi, provider);

      const [exists, id, name, org, email, owner, timestamp] = await verifier.verifyDocument(hash);
      if (!exists) throw new Error("File ch∆∞a ƒë∆∞·ª£c l∆∞u tr√™n blockchain.");

      const info = `‚úÖ File h·ª£p l·ªá!\nIssuer: ${name} (${org})\nEmail: ${email}\nOwner: ${owner}\nTime: ${new Date(
        Number(timestamp) * 1000
      ).toLocaleString()}`;
      setStatus(info);
      addHistory(`X√°c minh file: ${file.name} ‚úÖ`);
    } catch (err) {
      addError("verifyFile", err, "Ki·ªÉm tra xem file ƒë√£ ƒë∆∞·ª£c l∆∞u ch∆∞a.");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="container">
      <h1>üßæ Blockchain Document Verifier</h1>
      <button onClick={connectWallet} className="connect-btn">
        {account ? `‚úÖ ${account.slice(0, 6)}...${account.slice(-4)}` : "üîó K·∫øt n·ªëi MetaMask"}
      </button>

      <section>
        <h2>üè¢ ƒêƒÉng k√Ω Issuer</h2>
        <input placeholder="T√™n" onChange={(e) => setIssuer({ ...issuer, name: e.target.value })} />
        <input
          placeholder="T·ªï ch·ª©c"
          onChange={(e) => setIssuer({ ...issuer, organization: e.target.value })}
        />
        <input
          placeholder="Email"
          onChange={(e) => setIssuer({ ...issuer, email: e.target.value })}
        />
        <button onClick={registerIssuer} disabled={loading}>
          üìù ƒêƒÉng k√Ω
        </button>
      </section>

      <section>
        <h2>üìÅ X√°c minh / L∆∞u t√†i li·ªáu</h2>
        <input type="file" onChange={(e) => setFile(e.target.files[0])} />
        <select onChange={(e) => setSelectedIssuer(e.target.value)} value={selectedIssuer}>
          <option value="">-- Ch·ªçn Issuer --</option>
          {issuersList.map((i) => (
            <option key={i.id} value={i.id}>
              {i.name} ({i.org})
            </option>
          ))}
        </select>
        <button onClick={storeHash} disabled={loading}>
          üì§ L∆∞u Hash
        </button>
        <button onClick={verifyFile} disabled={loading}>
          üîç X√°c minh
        </button>
      </section>

      <pre className="status-box">{status}</pre>

      <section>
        <h2>üìú L·ªãch s·ª≠ ho·∫°t ƒë·ªông</h2>
        <ul className="history-list">
          {history.map((h, i) => (
            <li key={i} className={h.type}>
              <b>{h.time}</b> ‚Äî {h.msg}
            </li>
          ))}
        </ul>
      </section>

      <section>
