import React, { useState } from "react";
import { ethers } from "ethers";
import verifierAbi from "./abi/DocumentVerifier.json";
import registryAbi from "./abi/IssuerRegistry.json";

const VERIFIER_ADDRESS = "PASTE_DOCUMENT_VERIFIER_ADDRESS_HERE";
const REGISTRY_ADDRESS = "PASTE_ISSUER_REGISTRY_ADDRESS_HERE";

export default function App() {
  const [account, setAccount] = useState("");
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");
  const [issuerInfo, setIssuerInfo] = useState({
    name: "",
    organization: "",
    email: "",
  });

  async function connectWallet() {
    const provider = new ethers.BrowserProvider(window.ethereum);
    const [addr] = await provider.send("eth_requestAccounts", []);
    setAccount(addr);
  }

  async function registerIssuer() {
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });
      const signer = await provider.getSigner();
      const registry = new ethers.Contract(REGISTRY_ADDRESS, registryAbi, signer);

      const tx = await registry.registerIssuer(
        issuerInfo.name,
        issuerInfo.organization,
        issuerInfo.email
      );
      await tx.wait();
      setStatus("‚úÖ ƒêƒÉng k√Ω Issuer th√†nh c√¥ng!");
    } catch (err) {
      console.error(err);
      setStatus("‚ùå L·ªói khi ƒëƒÉng k√Ω Issuer.");
    }
  }

  async function storeHashOnChain() {
    try {
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi, signer);

      const tx = await contract.storeDocument(hash);
      await tx.wait();
      setStatus("‚úÖ L∆∞u hash th√†nh c√¥ng!");
    } catch (err) {
      console.error(err);
      setStatus("‚ùå L·ªói khi l∆∞u hash (ki·ªÉm tra ƒë√£ ƒëƒÉng k√Ω Issuer).");
    }
  }

  async function verifyFile() {
    try {
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi, signer);

      const [exists, issuerAddr, timestamp, issuerName, issuerOrg] =
        await contract.verifyDocument(hash);

      if (!exists) {
        setStatus("‚ùå File kh√¥ng t·ªìn t·∫°i tr√™n blockchain.");
      } else {
        setStatus(
          `‚úÖ File h·ª£p l·ªá!\nIssuer: ${issuerName} (${issuerOrg})\nAddress: ${issuerAddr}\nTime: ${new Date(
            Number(timestamp) * 1000
          ).toLocaleString()}`
        );
      }
    } catch (err) {
      console.error(err);
      setStatus("‚ùå L·ªói khi x√°c minh file.");
    }
  }

  return (
    <div style={{ fontFamily: "Arial", margin: "40px auto", maxWidth: "700px" }}>
      <h1>üè• Blockchain Document Verifier</h1>
      <button onClick={connectWallet}>
        {account
          ? `‚úÖ ƒê√£ k·∫øt n·ªëi: ${account.slice(0, 6)}...${account.slice(-4)}`
          : "üîó K·∫øt n·ªëi MetaMask"}
      </button>

      <hr />
      <h2>üè¢ ƒêƒÉng k√Ω Issuer</h2>
      <input placeholder="T√™n" onChange={(e) => setIssuerInfo({ ...issuerInfo, name: e.target.value })} />
      <input placeholder="T·ªï ch·ª©c" onChange={(e) => setIssuerInfo({ ...issuerInfo, organization: e.target.value })} />
      <input placeholder="Email" onChange={(e) => setIssuerInfo({ ...issuerInfo, email: e.target.value })} />
      <button onClick={registerIssuer}>ƒêƒÉng k√Ω</button>

      <hr />
      <h2>üìÑ X√°c minh t√†i li·ªáu</h2>
      <input type="file" onChange={(e) => setFile(e.target.files[0])} />
      <button onClick={storeHashOnChain}>üì§ L∆∞u Hash</button>
      <button onClick={verifyFile}>üîç X√°c minh</button>

      <pre style={{ background: "#f9f9f9", padding: "10px", marginTop: "20px" }}>{status}</pre>
    </div>
  );
}
