import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import verifierAbi from "./abi/DocumentVerifier.json";
import registryAbi from "./abi/IssuerRegistry.json";
import "./App.css";

const VERIFIER_ADDRESS = "0x610178dA211FEF7D417bC0e6FeD39F05609AD788";
const REGISTRY_ADDRESS = "0x8A791620dd6260079BF849Dc5567aDC3F2FdC318";

export default function App() {
  const [account, setAccount] = useState("");
  const [issuer, setIssuer] = useState({ name: "", organization: "", email: "" });
  const [issuersList, setIssuersList] = useState([]);
  const [selectedIssuer, setSelectedIssuer] = useState("");
  const [search, setSearch] = useState("");
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");
  const [loading, setLoading] = useState(false);

  const getProvider = async () => {
    const provider = new ethers.BrowserProvider(window.ethereum);
    provider.getNetwork = async () => ({ name: "hardhat", chainId: 31337, ensAddress: null });
    return provider;
  };

  // K·∫øt n·ªëi v√≠
  async function connectWallet() {
    if (!window.ethereum) return alert("‚ö†Ô∏è C√†i MetaMask tr∆∞·ªõc!");
    const provider = await getProvider();
    const [addr] = await provider.send("eth_requestAccounts", []);
    setAccount(addr);
  }

  // ƒêƒÉng k√Ω issuer
  async function registerIssuer() {
    try {
      setLoading(true);
      const provider = await getProvider();
      const signer = await provider.getSigner();
      const registry = new ethers.Contract(REGISTRY_ADDRESS, registryAbi.abi, signer);
      const tx = await registry.registerIssuer(issuer.name, issuer.organization, issuer.email);
      setStatus("‚è≥ ƒêang g·ª≠i giao d·ªãch ƒëƒÉng k√Ω...");
      await tx.wait();
      setStatus("‚úÖ ƒêƒÉng k√Ω Issuer th√†nh c√¥ng!");
      await loadIssuers();
    } catch (e) {
      console.error(e);
      setStatus("‚ùå L·ªói khi ƒëƒÉng k√Ω Issuer.");
    } finally {
      setLoading(false);
    }
  }

  // T·∫£i danh s√°ch issuer
  async function loadIssuers() {
    try {
      const provider = await getProvider();
      const registry = new ethers.Contract(REGISTRY_ADDRESS, registryAbi.abi, provider);
      const signer = await provider.getSigner();
      const addr = await signer.getAddress();

      const list = [];
      // Hardhat m·∫∑c ƒë·ªãnh ch·ªâ c√≥ 20 account, n√™n ta gi·∫£ ƒë·ªãnh c√°c Issuer ƒë√£ ƒëƒÉng k√Ω
      for (let i = 0; i < 20; i++) {
        const address = ethers.getAddress(
          "0xf39Fd6e51aad88F6F4ce6aB8827279cffFb92266".slice(0, -2) +
            i.toString(16).padStart(2, "0")
        );
        try {
          const [name, org, email] = await registry.getIssuer(address);
          if (name) list.push({ address, name, org, email });
        } catch {}
      }
      setIssuersList(list);
    } catch (e) {
      console.error("Load issuers error:", e);
    }
  }

  useEffect(() => {
    loadIssuers();
  }, []);

  // L∆∞u hash (v·ªõi issuer ch·ªçn)
  async function storeHash() {
    if (!file) return alert("Ch∆∞a ch·ªçn file!");
    if (!selectedIssuer) return alert("Ch∆∞a ch·ªçn Issuer!");
    try {
      setLoading(true);
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      const provider = await getProvider();
      const signer = await provider.getSigner();
      const verifier = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi.abi, signer);

      const tx = await verifier.storeDocumentWithIssuer(hash, selectedIssuer);
      setStatus("‚è≥ ƒêang l∆∞u hash...");
      await tx.wait();
      setStatus("‚úÖ L∆∞u hash th√†nh c√¥ng!");
    } catch (e) {
      console.error(e);
      if (e.message.includes("Already stored"))
        setStatus("‚ö†Ô∏è Hash ƒë√£ t·ªìn t·∫°i tr√™n blockchain!");
      else setStatus("‚ùå L·ªói khi l∆∞u hash.");
    } finally {
      setLoading(false);
    }
  }

  // X√°c minh file
  async function verifyFile() {
    if (!file) return alert("Ch∆∞a ch·ªçn file!");
    try {
      setLoading(true);
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));
      const provider = await getProvider();
      const verifier = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi.abi, provider);

      const [exists, issuerAddr, timestamp, name, org] = await verifier.verifyDocument(hash);
      if (!exists) return setStatus("‚ùå File ch∆∞a ƒë∆∞·ª£c l∆∞u tr√™n blockchain.");

      setStatus(
        `‚úÖ File h·ª£p l·ªá!\nIssuer: ${name} (${org})\nAddress: ${issuerAddr}\nTime: ${new Date(
          Number(timestamp) * 1000
        ).toLocaleString()}`
      );
    } catch (e) {
      console.error(e);
      setStatus("‚ùå L·ªói khi x√°c minh file.");
    } finally {
      setLoading(false);
    }
  }

  // L·ªçc danh s√°ch Issuer theo t·ª´ kh√≥a t√¨m ki·∫øm
  const filteredIssuers = issuersList.filter(
    (i) =>
      i.name.toLowerCase().includes(search.toLowerCase()) ||
      i.org.toLowerCase().includes(search.toLowerCase()) ||
      i.email.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="container">
      <h1>üßæ Blockchain Document Verifier</h1>
      <button onClick={connectWallet} className="connect-btn">
        {account ? `‚úÖ ${account.slice(0, 6)}...${account.slice(-4)}` : "üîó K·∫øt n·ªëi MetaMask"}
      </button>

      <section>
        <h2>üè¢ ƒêƒÉng k√Ω Issuer</h2>
        <input placeholder="T√™n" onChange={(e) => setIssuer({ ...issuer, name: e.target.value })} />
        <input
          placeholder="T·ªï ch·ª©c"
          onChange={(e) => setIssuer({ ...issuer, organization: e.target.value })}
        />
        <input placeholder="Email" onChange={(e) => setIssuer({ ...issuer, email: e.target.value })} />
        <button onClick={registerIssuer} disabled={loading}>
          üìù ƒêƒÉng k√Ω
        </button>
      </section>

      <section>
        <h2>üìÅ X√°c minh t√†i li·ªáu</h2>
        <input type="file" onChange={(e) => setFile(e.target.files[0])} />
        <div className="issuer-select">
          <input
            type="text"
            placeholder="üîç T√¨m Issuer..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <select onChange={(e) => setSelectedIssuer(e.target.value)} value={selectedIssuer}>
            <option value="">-- Ch·ªçn Issuer --</option>
            {filteredIssuers.map((i, idx) => (
              <option key={idx} value={i.address}>
                {i.name} ({i.org})
              </option>
            ))}
          </select>
        </div>
        <button onClick={storeHash} disabled={loading}>
          üì§ L∆∞u Hash
        </button>
        <button onClick={verifyFile} disabled={loading}>
          üîç X√°c minh
        </button>
      </section>

      <pre className="status-box">{status}</pre>
    </div>
  );
}
