import React, { useState } from "react";
import { ethers } from "ethers";
import verifierAbi from "./abi/DocumentVerifier.json";
import registryAbi from "./abi/IssuerRegistry.json";

// ‚öôÔ∏è D√°n 2 ƒë·ªãa ch·ªâ m·ªõi nh·∫•t sau khi deploy
const VERIFIER_ADDRESS = "0xe7f1725E7734CE288F8367e1Bb143E90bb3F0512"; // DocumentVerifier
const REGISTRY_ADDRESS = "0x5FbDB2315678afecb367f032d93F642f64180aa3"; // IssuerRegistry

export default function App() {
  const [account, setAccount] = useState("");
  const [issuer, setIssuer] = useState({ name: "", organization: "", email: "" });
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");

  // üü¢ K·∫øt n·ªëi v√≠ MetaMask
  async function connectWallet() {
    if (!window.ethereum) return alert("‚ö†Ô∏è C√†i MetaMask tr∆∞·ªõc!");
    const provider = new ethers.BrowserProvider(window.ethereum);
    const [addr] = await provider.send("eth_requestAccounts", []);
    setAccount(addr);
    setStatus("‚úÖ ƒê√£ k·∫øt n·ªëi v√≠.");
  }

  // üè¢ ƒêƒÉng k√Ω Issuer
  async function registerIssuer() {
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);

      // ‚öôÔ∏è Fix l·ªói ENS (localhost kh√¥ng c√≥ ENS)
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });

      const signer = await provider.getSigner();
      const contract = new ethers.Contract(REGISTRY_ADDRESS, registryAbi.abi, signer); // ‚úÖ .abi

      const tx = await contract.registerIssuer(
        issuer.name,
        issuer.organization,
        issuer.email
      );
      setStatus("‚è≥ ƒêang g·ª≠i giao d·ªãch ƒëƒÉng k√Ω...");
      await tx.wait();
      setStatus("‚úÖ ƒêƒÉng k√Ω Issuer th√†nh c√¥ng!");
    } catch (err) {
      console.error(err);
      setStatus("‚ùå L·ªói khi ƒëƒÉng k√Ω Issuer. Ki·ªÉm tra m·∫°ng ho·∫∑c ƒë·ªãa ch·ªâ contract.");
    }
  }

  // üìÑ L∆∞u hash t√†i li·ªáu
  async function storeHash() {
    if (!file) return alert("Ch∆∞a ch·ªçn file!");
    try {
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi.abi, signer); // ‚úÖ .abi

      const tx = await contract.storeDocument(hash);
      setStatus("‚è≥ ƒêang l∆∞u hash l√™n blockchain...");
      await tx.wait();
      setStatus("‚úÖ L∆∞u hash th√†nh c√¥ng!");
    } catch (err) {
      console.error(err);
      setStatus("‚ùå L·ªói khi l∆∞u hash (c√≥ th·ªÉ b·∫°n ch∆∞a ƒëƒÉng k√Ω Issuer).");
    }
  }

  // üîç X√°c minh t√†i li·ªáu
  async function verifyFile() {
    if (!file) return alert("Ch∆∞a ch·ªçn file!");
    try {
      const buffer = await file.arrayBuffer();
      const hash = ethers.sha256(new Uint8Array(buffer));

      const provider = new ethers.BrowserProvider(window.ethereum);
      provider.getNetwork = async () => ({
        name: "hardhat",
        chainId: 31337,
        ensAddress: null,
      });
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(VERIFIER_ADDRESS, verifierAbi.abi, signer); // ‚úÖ .abi

      const [exists, issuerAddr, timestamp, name, org] = await contract.verifyDocument(hash);

      if (!exists) return setStatus("‚ùå File ch∆∞a ƒë∆∞·ª£c l∆∞u tr√™n blockchain.");

      setStatus(
        `‚úÖ File h·ª£p l·ªá!\nIssuer: ${name} (${org})\nAddress: ${issuerAddr}\nTime: ${new Date(
          Number(timestamp) * 1000
        ).toLocaleString()}`
      );
    } catch (err) {
      console.error(err);
      setStatus("‚ùå L·ªói khi x√°c minh file.");
    }
  }

  return (
    <div style={{ fontFamily: "Arial", margin: "40px auto", maxWidth: "700px" }}>
      <h1>üßæ Blockchain Document Verifier</h1>

      <button onClick={connectWallet}>
        {account
          ? `‚úÖ ƒê√£ k·∫øt n·ªëi: ${account.slice(0, 6)}...${account.slice(-4)}`
          : "üîó K·∫øt n·ªëi MetaMask"}
      </button>

      <hr />

      <h2>üè¢ ƒêƒÉng k√Ω Issuer</h2>
      <input
        placeholder="T√™n"
        onChange={(e) => setIssuer({ ...issuer, name: e.target.value })}
      />
      <input
        placeholder="T·ªï ch·ª©c"
        onChange={(e) => setIssuer({ ...issuer, organization: e.target.value })}
      />
      <input
        placeholder="Email"
        onChange={(e) => setIssuer({ ...issuer, email: e.target.value })}
      />
      <button onClick={registerIssuer}>ƒêƒÉng k√Ω</button>

      <hr />

      <h2>üìÅ X√°c minh t√†i li·ªáu</h2>
      <input type="file" onChange={(e) => setFile(e.target.files[0])} />
      <div>
        <button onClick={storeHash}>üì§ L∆∞u Hash</button>
        <button onClick={verifyFile} style={{ marginLeft: "10px" }}>
          üîç X√°c minh
        </button>
      </div>

      <pre style={{ background: "#f4f4f4", padding: "15px", marginTop: "20px" }}>
        {status}
      </pre>
    </div>
  );
}
